function extract(rawResponse) {
  const data = JSON.parse(rawResponse);

  // Check if we have results
  if (!data.chart || !data.chart.result || data.chart.result.length === 0) {
    throw new Error('No chart data available');
  }

  const result = data.chart.result[0];
  const timestamps = result.timestamp || [];
  const quotes = result.indicators?.quote || [];
  const adjclose = result.indicators?.adjclose || [];

  if (timestamps.length === 0 || quotes.length === 0) {
    throw new Error('No historical price data available');
  }

  // Target date: March 2, 2026 (Unix timestamp 1772146800 for 00:00:00 EST)
  // But we need to find the trading day on or before March 2
  // March 2, 2026 is a Monday, so it should be a trading day
  const targetDate = 1772146800; // March 2, 2026 00:00:00 EST (5:00:00 UTC)

  // Find the closest timestamp to targetDate
  let closestIndex = -1;
  let minDiff = Infinity;

  for (let i = 0; i < timestamps.length; i++) {
    const diff = Math.abs(timestamps[i] - targetDate);
    if (diff < minDiff) {
      minDiff = diff;
      closestIndex = i;
    }
  }

  if (closestIndex === -1) {
    throw new Error('Could not find price data for target date');
  }

  // Use adjusted close price (accounts for splits)
  let closingPrice;
  if (adjclose.length > 0 && adjclose[0].adjclose && adjclose[0].adjclose[closestIndex] !== undefined) {
    closingPrice = adjclose[0].adjclose[closestIndex];
  } else if (quotes[0].close && quotes[0].close[closestIndex] !== undefined) {
    closingPrice = quotes[0].close[closestIndex];
  } else {
    throw new Error('Could not extract closing price for target date');
  }

  return closingPrice.toString();
}
