function extract(rawResponse) {
  var timeMatch = rawResponse.match(/Game Time[\s\S]*?(\d+:\d+)/);
  if (!timeMatch || timeMatch[1] === '0:00') {
    throw new Error('Game has not been played yet (game time is 0:00)');
  }
  var blueMatch = rawResponse.match(/blue-line-header">\s*<a[^>]*>([^<]+)<\/a>\s*-\s*(WIN|LOSS)/i);
  var redMatch = rawResponse.match(/red-line-header">\s*<a[^>]*>([^<]+)<\/a>\s*-\s*(WIN|LOSS)/i);
  if (!blueMatch || !redMatch) throw new Error('Could not find team results on page');
  var weiboResult;
  if (blueMatch[1].indexOf('Weibo') !== -1) {
    weiboResult = blueMatch[2];
  } else if (redMatch[1].indexOf('Weibo') !== -1) {
    weiboResult = redMatch[2];
  } else {
    throw new Error('Weibo Gaming not found in match');
  }
  return weiboResult === 'WIN' ? '1' : '0';
}
